      newContainer.className = 'w-screen h-500 bg-gray-500 bg-opacity-50 p-8 rounded-lg shadow-lg flex items-center justify-center';
      newContainer.style.marginTop = '4px'; // Added inline style for top margin
      document.body.appendChild(newContainer);
      renderGraph(newContainer, users, filter);
    } else {
      renderGraph(container, users, filter);
    }
