        hobbyMap[user.hobby] = [];
      }
      hobbyMap[user.hobby].push(user.id_num);

      if (!stateMap[user.state]) {
        stateMap[user.state] = [];
      }
      stateMap[user.state].push(user.id_num);
    });

    // Connect common workplaces
    if (filter === 'all' || filter === 'workplace') {
      Object.values(workplaceMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'blue' });
          }
        }
      });
    }

    // Connect common hobbies
