      Object.values(hobbyMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'green' });
          }
        }
      });
    }

    // Connect common states
    if (filter === 'all' || filter === 'state') {
      Object.values(stateMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'purple' });
          }
        }
      });
    }

    // Connect "ME" to users with the same workplace
    if (workplaceMap[me.workplace]) {
      workplaceMap[me.workplace].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'blue' });
      });
    }

    // Connect "ME" to users with the same hobby
    if (hobbyMap[me.hobby]) {
      hobbyMap[me.hobby].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'green' });
      });
    }

    // Connect "ME" to users with the same state
    if (stateMap[me.state]) {
      stateMap[me.state].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'purple' });
      });
    }

    const data = { nodes, edges };
