      nodes: {
        shape: 'dot',
        size: 32,
        font: {
          size: 32,
          color: '#314155',
        },
        borderWidth: 2,
      },
      edges: {
        width: 2,
      },
      physics: {
        enabled: true,
      },
    };

    new Network(container, data, options);
  };


