import React, { useState } from 'react';
import RegisterForm from '../registerPage/RegisterForm';

const SignInPage = ({ onSignInClick }) => {
  const [showRegister, setShowRegister] = useState(false);

  const SignInUser = () => {
    // Call the function passed from App component
    onSignInClick();
  };

  const SignUpClicked = (e) => {
    e.preventDefault(); // Prevent default link behavior
    setShowRegister(true);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 bg-opacity-50 py-6 px-4">
      <div className="bg-white shadow-lg rounded-lg max-w-md w-full p-8">
        {!showRegister ? (
          <form>
            <h2 className="text-2xl font-bold mb-6 text-center">Sign In</h2>
            
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="username">
                Username
              </label>
              <input
                id="username"
                type="text"
                placeholder="Username"
                className="shadow-sm appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div className="mb-6">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
                Password
              </label>
              <input
                id="password"
                type="password"
                placeholder="**********"
                className="shadow-sm appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div className="flex items-center justify-between mb-4">
              <button
                type="button"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={SignInUser}
              >
                Sign In
              </button>
              <a
                href="#"
                className="text-sm font-semibold text-blue-500 hover:text-blue-800"
              >
                Forgot Password?
              </a>
            </div>

            <div className="flex items-center justify-between p-4 bg-gray-100 mt-2 rounded-b-lg">
              <span className="block text-gray-700 text-sm">Not a member?</span>
              <a
                href="#"
                className="text-sm font-semibold text-blue-500 hover:text-blue-800"
                onClick={SignUpClicked}
              >
                Sign Up
              </a>
            </div>
          </form>
        ) : (
          <RegisterForm />
        )}
      </div>
    </div>
  );
};

export default SignInPage;
