import React, { useEffect, useState } from 'react';
import { DataSet, Network } from 'vis-network/standalone';
import 'vis-network/styles/vis-network.css';

const GraphComponent = () => {
  const [users, setUsers] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const userData = get();
    setUsers(userData);
  }, []);

  useEffect(() => {
    const container = document.getElementById('network-graph');
    if (!container) {
      const newContainer = document.createElement('div');
      newContainer.id = 'network-graph';
      newContainer.className = 'w-screen h-96 bg-gray-500 bg-opacity-50 p-8 rounded-lg shadow-lg flex items-center justify-center';
      newContainer.style.marginTop = '4px'; // Added inline style for top margin
      document.body.appendChild(newContainer);
      renderGraph(newContainer, users, filter);
    } else {
      renderGraph(container, users, filter);
    }
  }, [users, filter]);

  const renderGraph = (container, users, filter) => {
    const me = {
      id_num: '000000000', // Unique ID for "ME"
      firstName: 'Me',
      lastName: '',
      workplace: 'Tech Company Inc.',
      hobby: 'Reading',
      state: 'New York' // Added a state attribute for filtering
    };

    const relatedUsers = users.filter(user => 
      user.workplace === me.workplace || user.hobby === me.hobby || user.state === me.state
    );

    const nodes = new DataSet([
      ...relatedUsers.map(user => ({
        id: user.id_num,
        label: `${user.firstName} ${user.lastName}`
      })),
      { id: me.id_num, label: 'Me', color: { background: 'red', border: 'black' }, size: 40 } // Customize "ME" node
    ]);

    const edges = new DataSet();
    const workplaceMap = {};
    const hobbyMap = {};
    const stateMap = {};

    relatedUsers.forEach(user => {
      if (!workplaceMap[user.workplace]) {
        workplaceMap[user.workplace] = [];
      }
      workplaceMap[user.workplace].push(user.id_num);

      if (!hobbyMap[user.hobby]) {
        hobbyMap[user.hobby] = [];
      }
      hobbyMap[user.hobby].push(user.id_num);

      if (!stateMap[user.state]) {
        stateMap[user.state] = [];
      }
      stateMap[user.state].push(user.id_num);
    });

    // Connect common workplaces
    if (filter === 'all' || filter === 'workplace') {
      Object.values(workplaceMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'blue' });
          }
        }
      });
    }

    // Connect common hobbies
    if (filter === 'all' || filter === 'hobby') {
      Object.values(hobbyMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'green' });
          }
        }
      });
    }

    // Connect common states
    if (filter === 'all' || filter === 'state') {
      Object.values(stateMap).forEach(ids => {
        for (let i = 0; i < ids.length - 1; i++) {
          for (let j = i + 1; j < ids.length; j++) {
            edges.add({ from: ids[i], to: ids[j], color: 'purple' });
          }
        }
      });
    }

    // Connect "ME" to users with the same workplace
    if (workplaceMap[me.workplace]) {
      workplaceMap[me.workplace].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'blue' });
      });
    }

    // Connect "ME" to users with the same hobby
    if (hobbyMap[me.hobby]) {
      hobbyMap[me.hobby].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'green' });
      });
    }

    // Connect "ME" to users with the same state
    if (stateMap[me.state]) {
      stateMap[me.state].forEach(id => {
        edges.add({ from: me.id_num, to: id, color: 'purple' });
      });
    }

    const data = { nodes, edges };
    const options = {
      nodes: {
        shape: 'dot',
        size: 32,
        font: {
          size: 32,
          color: '#314155',
        },
        borderWidth: 2,
      },
      edges: {
        width: 2,
      },
      physics: {
        enabled: true,
      },
    };

    new Network(container, data, options);
  };


  const get = () => {
    users = fetch('/users.json')
    .then(response => response.json())
    .then(data => setData(data))
    .catch(error => console.error('Error fetching the JSON data:', error));
    return users;
  };

  function handleFilterChange(newFilter) {
    setFilter(newFilter);
  }
  
  return (
    <div>
      <div className="tabs">
        <button onClick={() => handleFilterChange('all')}>All</button>
        <button onClick={() => handleFilterChange('hobby')}>Hobby</button>
        <button onClick={() => handleFilterChange('workplace')}>Workplace</button>
        <button onClick={() => handleFilterChange('state')}>State</button>
      </div>
      <div id="network-graph"></div>
    </div>
  );
};

export default GraphComponent;
