      workplace: 'Tech Company Inc.',
      hobby: 'Reading'
    };

    const nodes = new DataSet([
      ...users.map(user => ({
        id: user.id_num,
        label: `${user.firstName} ${user.lastName}`
      })),
      { id: me.id_num, label: 'Me' } // Adding "ME" node
    ]);

    const edges = new DataSet();
    const workplaceMap = {};
    const hobbyMap = {};

    users.forEach(user => {
      if (!workplaceMap[user.workplace]) {
        workplaceMap[user.workplace] = [];
      }
