        }
      }
    });

    // Connect "ME" to users with the same workplace
    if (workplaceMap[me.workplace]) {
      workplaceMap[me.workplace].forEach(id => {
        edges.add({ from: me.id_num, to: id });
      });
    }

    // Connect "ME" to users with the same hobby
    if (hobbyMap[me.hobby]) {
      hobbyMap[me.hobby].forEach(id => {
        edges.add({ from: me.id_num, to: id });
      });
    }

    const data = { nodes, edges };
    const options = {
      nodes: {
        shape: 'dot',
        size: 32,
        font: {
          size: 32,
          color: '#314155',
        },
        borderWidth: 2,
      },
      edges: {
        width: 2,
      },
      physics: {
        enabled: true,
      },
    };

    new Network(container, data, options);
  };

  return null;
};

export default GraphComponent;
