    const container = document.getElementById('network-graph');
    if (container) {
      renderGraph(container, users, filter);
    } else {
      const newContainer = document.createElement('div');
      newContainer.id = 'network-graph';
      newContainer.className = 'w-full h-600 bg-gray-500 bg-opacity-50 p-8 rounded-lg shadow-lg flex items-center justify-center';
      newContainer.style.marginTop = '4px';
      document.body.appendChild(newContainer);
      renderGraph(newContainer, users, filter);
    }
  }, [users, filter]);

  const renderGraph = (container, users, filter) => {
    const me = {
      id_num: '000000000',
      firstName: 'Me',
      lastName: '',
      workplace: 'Tech Company Inc.',
      hobby: 'Reading',
      state: 'New York'
    };

    const filteredUsers = filterUsers(users, me, filter);
    const nodes = createNodes(filteredUsers, me);
    const edges = createEdges(filteredUsers, me, filter);

    const data = { nodes, edges };
    const options = getGraphOptions();

    new Network(container, data, options);
  };

  const filterUsers = (users, me, filter) => {
    return users.filter(user => {
      if (filter === 'all') {
        return user.workplace === me.workplace || user.hobby === me.hobby || user.state === me.state;
      }
      if (filter === 'workplace') return user.workplace === me.workplace;
      if (filter === 'hobby') return user.hobby === me.hobby;
      if (filter === 'state') return user.state === me.state;
      return false;
    });
  };

  const createNodes = (users, me) => {
    return new DataSet([
      ...users.map(user => ({
        id: user.id_num,
        label: `${user.firstName} ${user.lastName}`
      })),
      { id: me.id_num, label: 'Me', color: { background: 'red', border: 'black' }, size: 40 }
    ]);
  };

  const createEdges = (users, me, filter) => {
    const edges = new DataSet();
    const map = {
      workplace: {},
      hobby: {},
      state: {}
    };

    users.forEach(user => {
      addToMap(map.workplace, user.workplace, user.id_num);
      addToMap(map.hobby, user.hobby, user.id_num);
      addToMap(map.state, user.state, user.id_num);
    });

    if (filter === 'all' || filter === 'workplace') createEdgesForMap(edges, map.workplace, 'blue');
    if (filter === 'all' || filter === 'hobby') createEdgesForMap(edges, map.hobby, 'green');
    if (filter === 'all' || filter === 'state') createEdgesForMap(edges, map.state, 'purple');

    addEdgesForMe(edges, me, map.workplace[me.workplace], 'blue');
    addEdgesForMe(edges, me, map.hobby[me.hobby], 'green');
    addEdgesForMe(edges, me, map.state[me.state], 'purple');

    return edges;
  };

  const addToMap = (map, key, value) => {
    if (!map[key]) map[key] = [];
    map[key].push(value);
  };

